import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'

import { getDoc , query,collection, where, getDocs ,doc ,updateDoc , addDoc , onSnapshot} from 'firebase/firestore'; // Assuming Firebase v9
import { db  } from '../firebase.js';

export default function Orders() {
const {data:storeId} = useParams()

const [orders, setOrders] = useState([]); 
  
  
const q = query(collection(db, "Orders"), where("storeId", "==", storeId));
const unsubscribe = onSnapshot(q, (querySnapshot) => {
  const ordrs = [];
  querySnapshot.forEach((doc) => {
      ordrs.push({...doc.data()});
  });
  setOrders(ordrs);

});



return (
    <div className={"p-4"}>
      <h3 className={"mb-4"}>Store Orders</h3>

   
   
      
      {orders.length > 0 ? (
         <Table striped bordered hover responsive  className={"shadow-sm "} draggable   >

         <thead>
           <tr className={"text-center "}>
             <th>orderId</th>
             <th>Status</th>
             <th>CreatedAt</th>
             <th>Total Items</th>
             <th>Total Price</th>
            
           </tr>
         </thead>
         <tbody >
          {products.map((product) => (
          <tr key={product.productId} className={"text-center flex align-middle"} onClick={()=>navigate(`/productInfo/${product.productId}`)}>
         <td  className="d-flex align-items-center justify-content-center" >  
          <Image src={product.image} thumbnail  width={45} height={45}   />
        </td>
          <td>{product.name}</td>
          <td>{product.category}</td>
          <td>{product.price}</td>
          <td>{product.discount}%</td>
          <td>{product.inventory}</td>
          
          {product.inventory > 0 ? (
  <td className="text-success">
    <p>In Stock</p>
  </td>
) : (
  <td className="text-danger">Out of Stock</td>
)}

          </tr>
         
            
          
          ))}
          </tbody>
         </Table>
      
      ) : (
        <p>No products found.</p>
      )}
    </div>
  );
}
