import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getDocs, doc , query,collection, where  } from 'firebase/firestore'; // Assuming Firebase v9
import { db } from '../firebase.js';

export default function ProductInfo() {
  const { data: productId } = useParams(); // Extract product ID from route parameter
  const [productInfo, setProductInfo] = useState(null); // State for product data

  // Fetch product data from Firebase on component mount and whenever productId changes
  useEffect(() => {
    const getProductInfo = async () => {
      if (!productId) return; // Handle missing product ID gracefully

      try {
        const q = query(collection(db, "Products"), where("productId", "==", productId));
        const snapshot = await getDocs(q);

        const retrievedProduct = [];
        snapshot.forEach((doc) => {
          retrievedProduct.push({id: doc.id, ...doc.data() }); // Add product data with ID
        });
        setProductInfo(retrievedProduct);
      
      } catch (error) {
        console.error('Error fetching product info:', error); // Handle errors
      }
    };

    getProductInfo();
  }, []); // Dependency array ensures effect runs on product ID change

  // Display product info or loading/error message
  return (
    <div>
      {productInfo ? (
        <>
          <h2>Product Information</h2>
          <p>Name: {productInfo.name}</p>
          {/* Access other product properties from productInfo */}
        </>
      ) : (
        productInfo === null ? (
          <p>Loading product info...</p>
        ) : (
          <p>Product not found.</p>
        )
      )}
    </div>
  );
}
