import React from 'react'
import { useParams } from 'react-router-dom'

import { getDoc , query,collection, where, getDocs ,doc ,updateDoc , addDoc} from 'firebase/firestore'; // Assuming Firebase v9
import { db  } from '../firebase.js';

export default function Orders() {
const {data:storeId} = useParams()

const {orders , setOrders} = useState()

const orderRef= query(collection(db, "Products"), where("storeId", "==", storeId) , where("status"=="pending"));
const orderSnpshot = onSnapshot(orderRef, (querySnapshot) => {
  const orders = [];
  querySnapshot.forEach((doc) => {
      orders.push({productId: doc.id, ...doc.data()});
  });
  setOrders(orders);

});



  return (
    <div>{orders.length}</div>
  )
}
