import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'

import { getDoc , query,collection, where, getDocs ,doc ,updateDoc , addDoc , onSnapshot} from 'firebase/firestore'; // Assuming Firebase v9
import { db  } from '../firebase.js';

export default function Orders() {
const {data:storeId} = useParams()

const {orders , setOrders} = useState({})

const orderRef= query(collection(db, "Orders"), where("storeId", "==", storeId) );
const orderSnpshot = onSnapshot(orderRef, (querySnapshot) => {
  const ord = [];
  querySnapshot.forEach((doc) => {
      orders.push({productId: doc.id, ...doc.data()});
  });
  setOrders(ord);

});



  return (
    <div>{orders}</div>
  )
}
